$(document).ready(function(){let e=document.querySelector("body"),t=document.querySelector(".menu"),o=document.querySelectorAll(".menu__list-link"),l=document.querySelector(".menu__btn");function n(){t.classList.toggle("toggle"),l.classList.toggle("toggle"),e.classList.toggle("toggle")}l.addEventListener("click",n),o.forEach(e=>{e.addEventListener("click",function(){t.classList.contains("toggle")&&n()})});for(let t of document.querySelectorAll('a[href*="#"]'))t.addEventListener("click",function(e){e.preventDefault();e=t.getAttribute("href").substr(1);document.getElementById(e).scrollIntoView({behavior:"smooth",block:"start"})})});