$(document).ready(function(){let e=document.querySelector(".menu"),t=document.querySelectorAll(".menu__list-link"),l=document.querySelector(".menu__btn");function n(){e.classList.toggle("toggle"),l.classList.toggle("toggle")}l.addEventListener("click",n),t.forEach(e=>{e.addEventListener("click",n)});for(let t of document.querySelectorAll('a[href*="#"]'))t.addEventListener("click",function(e){e.preventDefault();e=t.getAttribute("href").substr(1);document.getElementById(e).scrollIntoView({behavior:"smooth",block:"start"})})});